        {
            return 500;
        }else if(timeBetweenMatches <= 50)
        {
            return 400;
        }else if(timeBetweenMatches <= 70)
        {
            return 300;
        }else if(timeBetweenMatches <= 100)
        {
            return 200;
        }else
        {
            return 100;
        }
    }


